(function() {
	'use strict';

	angular
		.module('app')
		.controller('DetailCtrl', DetailCtrl);

	DetailCtrl.$inject = ['$scope', '$routeParams', 'Metadata', 'APIData'];

	function DetailCtrl($scope, $routeParams, Metadata, APIData) {
		// controllerAs ViewModel
		var detail = this;

		// private variables
		var _id = $routeParams.id;

		_init();

		/**
		 * INIT function executes procedural code
		 *
		 * @private
		 */
		function _init() {
			_activate();
		}

		/**
		 * Controller activate
		 * Get JSON data
		 *
		 * @returns {*}
		 * @private
		 */
		function _activate() {
			// start loading
			detail.loading = true;

			// get the data from JSON
			return APIData.getDino(_id).then(_getJsonSuccess, _getJsonError);
		}

		/**
		 * Successful promise data
		 *
		 * @param data {json}
		 * @private
		 */
		function _getJsonSuccess(data) {
			detail.dino = data;
			detail.title = detail.dino.name;

			// set page <title>
			Metadata.set(detail.title);

			// stop loading
			detail.loading = false;

			return detail.dino;
		}

		/**
		 * Failure of promise data
		 * Show error 
		 * Stop loading
		 */
		function _getJsonError() {
			detail.error = true;

			// stop loading
			detail.loading = false;
		}
	}
}());
